import React from 'react';
import { MessageCircle, Database, Smartphone } from 'lucide-react';
import './ModeSelector.css';

function ModeSelector({ onModeSelect }) {
  const modes = [
    {
      id: 'chat',
      title: 'Chat with AI',
      description: 'Ask questions and get AI-powered responses',
      icon: MessageCircle,
      color: '#667eea',
      gradient: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
    },
    {
      id: 'store',
      title: 'Store Knowledge',
      description: 'Upload documents and text to build your knowledge base',
      icon: Database,
      color: '#2ed573',
      gradient: 'linear-gradient(135deg, #2ed573 0%, #7bed9f 100%)'
    },
    {
      id: 'whatsapp',
      title: 'WhatsApp Sync',
      description: 'Connect to WhatsApp for seamless messaging',
      icon: Smartphone,
      color: '#ff6b6b',
      gradient: 'linear-gradient(135deg, #ff6b6b 0%, #ffa8a8 100%)'
    }
  ];

  return (
    <div className="mode-selector">
      <div className="mode-selector-header">
        <h2>Choose Your Mode</h2>
        <p>Select how you want to interact with the AI Assistant</p>
      </div>
      
      <div className="mode-grid">
        {modes.map((mode) => {
          const IconComponent = mode.icon;
          return (
            <div
              key={mode.id}
              className="mode-card"
              onClick={() => onModeSelect(mode.id)}
              style={{ '--mode-color': mode.color, '--mode-gradient': mode.gradient }}
            >
              <div className="mode-icon">
                <IconComponent size={32} />
              </div>
              <h3>{mode.title}</h3>
              <p>{mode.description}</p>
              <div className="mode-arrow">â†’</div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default ModeSelector;
