import React, { useState } from 'react';
import ChatInterface from './components/ChatInterface';
import ModeSelector from './components/ModeSelector';
import { ChatProvider } from './context/ChatContext';
import './index.css';

function App() {
  const [currentMode, setCurrentMode] = useState(null);
  const [isConnected, setIsConnected] = useState(false);

  return (
    <ChatProvider>
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-500 via-purple-500 to-pink-500 p-4">
        <div className="w-full max-w-5xl h-[90vh] rounded-2xl overflow-hidden shadow-2xl backdrop-blur-xl bg-white/80 border border-white/30">
          <header className="px-6 py-4 border-b border-white/30 bg-gradient-to-r from-indigo-600 to-purple-600 text-white flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-bold tracking-tight">AI Assistant</h1>
              <p className="text-sm opacity-90">
                Chat, sync WhatsApp, or store knowledge.
              </p>
            </div>
            <div className="flex items-center gap-2 text-sm">
              <span
                className={`w-2 h-2 rounded-full ${
                  isConnected ? 'bg-green-400' : 'bg-gray-400'
                } animate-pulse`}
              />
              {isConnected ? 'Connected' : 'Disconnected'}
            </div>
          </header>

          <main className="h-[calc(100%-64px)] bg-gray-50">
            {!currentMode ? (
              <ModeSelector onModeSelect={setCurrentMode} />
            ) : (
              <ChatInterface
                mode={currentMode}
                onBack={() => setCurrentMode(null)}
                onConnectionChange={setIsConnected}
              />
            )}
          </main>
        </div>
      </div>
    </ChatProvider>
  );
}

export default App;
